
# Part 2

class Elavator
  def self.next_floor(current_floor:, instruction:)
    instruction == "(" ? current_floor += 1 : current_floor -= 1
  end
end

class Instructions
  attr_accessor :instruction_text, :processed_floors

  def initialize(instruction_text)
    @instruction_text = instruction_text
    @processed_floors = process_instructions!
  end

  def answer(floor_number)
    processed_floors.index { |floor| floor == floor_number }
  end

  def process_instructions!
    indexed_parts.each_with_index.inject([0]) do |arr, (instruction, i)|
      arr.push(Elavator.next_floor(current_floor: arr[i],
                                   instruction: instruction))
    end
  end

  private

  def indexed_parts
    instruction_text.split('')
  end
end

